mapper.select:
SELECT * FROM `plan_result`
WHERE 1=1

    and result_status = #{resultStatus}



    and plan_name=#{planName}



    and start_time LIKE concat(#{startTime},'%')


ORDER BY `id` DESC

    LIMIT #{page}, #{size}

SELECT COUNT(id) FROM plan_result WHERE 1=1

    and result_status = #{resultStatus}



    and plan_name=#{planName}


    and start_time LIKE concat(#{startTime},'%')
